          <h5 class="card-title text-center">{item.Title}</h5>
          <a
            href={`https://nearweek.com/news/${item.slug}`}
            class="mt-auto btn btn-primary"
          >
            View
          </a>
        </div>
      </div>
    </div>
  );
};

let news = [];

for (let i = 0; i < res.body.data.length; i++) {
  const item = res.body.data[i];
  news.push(Card(item));
}

return (
  <div>
