function time_diff_to_string(time_diff) {
  const hours = Math.floor(time_diff / 60 / 60);
  const minutes = Math.floor((time_diff / 60) % 60);
  const seconds = Math.floor(time_diff % (60 * 60));
  return `${hours}h ${minutes}m ${seconds}s`;
}

function get_window_status(first_day, timeout, day_length) {
  const now = Math.floor(Date.now() / 1000);
  if (now < first_day) {
    return "The challenge hasn't started yet!";
  }

  const time_diff = now - first_day;
  const days_passed = Math.floor(time_diff / day_length);
  // We're counting time since the first_day.
  // The window is open in [0, timeout] seconds time of day.
  const time_of_day = now - first_day - days_passed * day_length;
  if (time_of_day < timeout) {
    const time_left = timeout - time_of_day;
    return `The wake up window is open! It will remain open for another ${time_diff_to_string(
      time_left
    )}`;
  } else {
    const time_left = first_day + (days_passed + 1) * day_length - now;
    return `The wake up window is closed. It will open in ${time_diff_to_string(
      time_left
    )}`;
  }
}

