    );
  }
}

function formatDate(epoch, output) {
  const dateObj = new Date(epoch / 1000000);
  let humanDate = null;
  if (output === "datetime") {
    humanDate = dateObj.toLocaleString();
  } else {
    humanDate = dateObj.toLocaleDateString();
  }
  return humanDate;
}

