const TGAS_300 = '300000000000000';
// const ONE_NEAR = '1000000000000000000000000';
const ONE_HALF_NEAR = '500000000000000000000000';

function callContract(data) {
  const {
    name,
    type,
    category,
    status,
    start_date,
    end_date,
    location,
    images,
    links,
    description,
  } = data;
  Near.call(
    EVENTS_CONTRACT,
    'create_event',
    {
      account_id: accountId,
      name,
      type,
      category,
      status,
      start_date,
      end_date,
      location,
      images,
      links,
      description,
    },
    TGAS_300,
    ONE_HALF_NEAR
  );
}

function onSave(data) {
  console.log('onSave', data);

  callContract(data);
}

