const proxyMinter = "dev-1662838950242-10877157053447";
const nftContractId = "minsta.mintspace2.testnet";
const mbGraphEndpoint = "https://graph.mintbase.io/testnet";

const ipfsUrl = (cid) => `https://ipfs.near.social/ipfs/${cid}`;

let posts = [];

const data = fetch(mbGraphEndpoint, {
  method: "POST",
  headers: {
    "mb-api-key": "omni-site",
    "Content-Type": "application/json",
    "x-hasura-role": "anonymous",
  },
  body: JSON.stringify({
    query: `
  query FetchFeedMintedThings($accountId: String!, $contractAddress: String) {
    token: mb_views_nft_tokens(
      where: {
        minter: { _eq: $accountId }
        nft_contract_id: { _eq: $contractAddress }
        burned_timestamp: { _is_null: true }
      }
      order_by: { minted_timestamp: desc }
      limit: 3
    ) {
      id: token_id
      createdAt: minted_timestamp
      media
      title
      description
      metadata_id
    }
  }
`,
    variables: {
      accountId: proxyMinter,
      contractAddress: nftContractId,
    },
  }),
});

const handleImageUpload = (files) => {
  if (files?.length > 0) {
    State.update({
      img: {
        uploading: true,
        cid: null,
      },
    });
    const body = files[0];
    asyncFetch("https://ipfs.near.social/add", {
      method: "POST",
      headers: {
        Accept: "application/json",
      },
      body,
    }).then((res) => {
      const cid = res.body.cid;

      State.update({
        img: {
          cid,
        },
      });

      handleMint(cid);
    });
  } else {
    State.update({
      img: null,
    });
  }
};

const handleMint = (cid) => {
  const gas = 200000000000000;
  const deposit = 0;

  Near.call([
    {
      contractName: proxyMinter,
      methodName: "mint",
      args: {
        owner_id: accountId,
        metadata: {
          media: ipfsUrl(cid),
        },
        num_to_mint: 1,
        royalty_args: {
          split_between: {
            [accountId]: 10000,
          },
          percentage: 1000,
        },
        split_owners: null,
        nft_contract_id: nftContractId,
      },
      gas: gas,
      deposit: deposit,
    },
  ]);
};

