  ctx.arc(150, 150, 105, 0, Math.PI * 2, false); // Earth orbit
  ctx.stroke();

  ctx.drawImage(sun, 0, 0, 300, 300);

}

const animate = () => {
  setTimeout(function() {
      data.canvasImage = canvas.toDataURL("image/png").replace("image/png", "image/octet-stream")
    sendState();
    draw()
    requestAnimationFrame(animate);
  }, 1000 / 60);
}

const sendState = () => {
  window.top.postMessage(data, "*")
}

animate()
init()
</script>
`;

