const UPDATE_INTERVAL = props.updateInterval ?? 10000;
const getProgress = () => {
  const date = new Date();
  const months = [
    31,
    28 +
      (date.getFullYear() % 4 === 0 ||
      (date.getFullYear() % 400 === 0 && date.getFullYear() % 100 !== 0)
        ? 1
        : 0),
    31,
    30,
    31,
    30,
    31,
    31,
    30,
    31,
    30,
    31,
  ];
  const yearProgress = () => {
    let sum = 0;

    months.forEach(
      (month, index) =>
        (sum +=
          index < date.getMonth()
            ? month
            : index === date.getMonth()
            ? date.getDate()
            : 0)
    );

    sum--;
    sum +=
      (date.getHours() + date.getMinutes() / 60 + date.getSeconds() / 3600) /
      24;

    const yearDays =
      date.getFullYear() % 4 === 0 ||
      (date.getFullYear() % 400 === 0 && date.getFullYear() % 100 !== 0)
        ? 366
        : 365;

    return (sum / yearDays) * 100;
  };

  const monthProgress = () => {
    let sum = date.getDate() - 1;

    sum +=
      (date.getHours() + date.getMinutes() / 60 + date.getSeconds() / 3600) /
      24;

    return (sum / months[date.getMonth()]) * 100;
  };

  const dayProgress =
    ((date.getHours() + date.getMinutes() / 60 + date.getSeconds() / 3600) /
      24) *
    100;

  const workDayProgress = () => {
    let timeRange = 0;
    let timeDay = "";

    if (date.getHours() < 6) timeDay = "Early Morning";
    else if (date.getHours() < 12) {
      timeDay = "Morning";
      timeRange = 6;
    } else if (date.getHours() < 18) {
      timeDay = "Afternoon";
      timeRange = 12;
    } else {
      timeDay = "Night";
      timeRange = 18;
    }

    return {
      timeDay,
      progress:
        ((date.getHours() -
          timeRange +
          date.getMinutes() / 60 +
          date.getSeconds() / 3600) /
          6) *
        100,
    };
  };

  return {
    yearProgress: yearProgress(),
    monthProgress: monthProgress(),
    dayProgress: dayProgress,
    workDayProgress: workDayProgress(),
  };
};

