const createThing = () => {
  asyncFetch(API_URL, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      query: `
        mutation CreateIdea($type: String, $title: String, $widget: String) {
          things {
            create(name: $widget, type: {name: {is: $type}}) {
              message
            }
            addIdea(name: $title) {
              entities {
                  id
                }
            } 
          }
        }
      `,
      variables: {
        type: "idea",
        title: state.title,
        widget: state.widget,
      },
    }),
  }).then((res) => {
    // Catch if there is an error
    const ideaId = res.body.data?.things?.addIdea.entities[0].id;
    appendDescription(ideaId);
  });
};

const appendDescription = (ideaId) => {
  asyncFetch(API_URL, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      query: `
            mutation AppendDescription($ideaId: ID, $description: String) {
            ideas(id: {is: $ideaId}) {
                appendContentToDescription(value: $description) {
                    message
                    }
                }
            }
        `,
      variables: {
        ideaId: ideaId,
        description: state.description,
      },
    }),
  }).then((res) => {
    State.update({
      res: res,
    });
  });
};

