const createThing = () => {
  asyncFetch(API_URL, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      query: `
        mutation CreateEvent($type: String, $title: String, $widget: String) {
          things {
            create(name: $widget, type: {name: {is: $type}}) {
              message
            }
            addEvent(name: $title) {
              entities {
                  id
                }
            } 
          }
        }
      `,
      variables: {
        type: TYPE,
        title: state.title,
        widget: state.widget,
      },
    }),
  }).then((res) => {
    // Catch if there is an error
    const eventId = res.body.data?.things?.addEvent.entities[0].id;
    appendDescription(eventId);
  });
};

const appendDescription = (eventId) => {
  asyncFetch(API_URL, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      query: `
            mutation AppendDescription($eventId: ID, $description: String) {
            events(id: {is: $eventId}) {
                appendContentToDescription(value: $description) {
                    message
                    }
                }
            }
        `,
      variables: {
        eventId: eventId,
        description: state.description,
      },
    }),
  }).then((res) => {
    State.update({
