  });
};

const getLittleLinkAccountRegisteredInFactory = (creator) => {
  if (!creator) return null;

  try {
    const data = Near.view(
      FACTORY_CONTRACT,
      "get_registered_contract_by_creator",
      {
        creator_id: creator,
      }
    );

    return data || null;
  } catch (err) {
    console.error(`Couldn't get little link account from factory`);
    throw err;
  }
};

const getAccountAccessKeys = (account) => {
  const response = fetch(RPC_URL, {
    method: "POST",
    headers: {
      ["Content-Type"]: "application/json",
      ["Accept"]: "application/json",
    },
    body: JSON.stringify({
      jsonrpc: "2.0",
      id: "dontcare",
      method: "query",
      params: {
        request_type: "view_access_key_list",
        finality: "final",
        account_id: account,
      },
    }),
  });

  if (!response.body.result) {
    return [];
  }

  return response.body.result.keys;
};

const createContract = () => {
  if (!state.title) {
    return setError(`Title can't be empty`);
  }

  if (!state.description) {
    return setError(`Description can't be empty`);
  }

  const keys = getAccountAccessKeys(context.accountId);

  const fullAccessKey = keys.find(
    (key) => key.access_key.permission === "FullAccess"
  );

  if (!fullAccessKey) {
    setError("Couldn't find FullAccessKey pair within your account");
    return;
  }

  console.log("key", fullAccessKey);

  const fields = {
    title: state.title,
    description: state.description,
    twitter: state.twitter,
    telegram: state.telegram,
    github: state.github,
    medium: state.medium,
  };

  const createResponse = Near.call(
    FACTORY_CONTRACT,
    "create_web4_little_link_page",
    {
      args: { data: fields },
      pub_key: fullAccessKey.public_key,
    },
    300 * TGAS
  );

  console.log("create", createResponse);
};

const updateContract = () => {
  const keys = getAccountAccessKeys(context.accountId);

  const fullAccessKey = keys.find(
    (key) => key.access_key.permission === "FullAccess"
  );

  if (!fullAccessKey) throw new Error(`No full access key found`);

  console.log("key", fullAccessKey);

  const fields = {
    title: state.title,
    description: state.description,
    twitter: state.twitter,
    telegram: state.telegram,
    github: state.github,
    medium: state.medium,
  };

  const createResponse = Near.call(
    FACTORY_CONTRACT,
    "create_web4_little_link_page",
    {
      args: { data: fields },
      pub_key: fullAccessKey.public_key,
    },
    300 * TGAS
  );

  console.log("create", createResponse);
};

