            <div class="table-responsive mt-3">
              <table
                class="table-striped table"
                style={{
                  padding: "30px",
                  "table-layout": "fixed",
                }}
              >
                <thead>
                  <tr>
                    <th>Function Name</th>
                    <th>Key Name</th>
                    <th>Value</th>
                  </tr>
                </thead>
                <tbody>
                  {state.indexer_res.map((x) => (
                    <tr>
                      <td>{x.function_name}</td>
                      <td>{x.key_name}</td>
                      <TableElement>{x.value}</TableElement>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            <Widget
              src="roshaan.near/widget/Paginate-fork"
              props={{
                siblingCount: 1,
                totalCount: state.indexer_resCount,
                pageSize: LIMIT,
                onPageChange: onIndexerResPageChange,
                currentPage: state.indexer_resPage,
              }}
            />
          </div>
        ) : (
          <Subheading> No data to show... </Subheading>
        )}

        <SmallTitle>
          Indexer State{" "}
          <button onClick={() => refetchIndexerState()}>
            <i class="bi bi-arrow-clockwise"></i>
          </button>
        </SmallTitle>
        <Subheading>
          {`${state.indexerToNetworkTipGap} blocks behind Mainnet network` ??
            "Loading..."}
        </Subheading>
        {state.state.length > 0 ? (
          <div class="table-responsive mt-3">
            <table
              class="table-striped table"
              style={{
                padding: "30px",
                "table-layout": "fixed",
              }}
            >
              <thead>
                <tr>
                  <th>Function Name</th>
                  <th>Current Block Height</th>
                </tr>
              </thead>
              <tbody>
                {state.state.map((x) => (
                  <tr>
                    <TableElement>{x.function_name}</TableElement>
                    <td>{x.current_block_height}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <Subheading> No data to show... </Subheading>
        )}
        <SmallTitle> Indexer Logs</SmallTitle>
        {state.logs.length > 0 ? (
          <div>
            <div class="table-responsive mt-3">
              <table
                class="table-striped table"
                style={{
                  padding: "30px",
                  "table-layout": "fixed",
                }}
              >
                <thead>
                  <tr>
                    <th>Block Height</th>
                    <th>Function Name</th>
                    <th>ID</th>
                    <th>Message</th>

                    <th>Timestamp</th>
                  </tr>
                </thead>
                <tbody>
                  {state.logs.map((x) => (
                    <tr>
                      <TableElement>{x.block_height}</TableElement>
                      <TableElement>{x.function_name}</TableElement>
                      <TableElement>{x.id}</TableElement>
                      <TableElement>{x.message}</TableElement>
                      <TableElement>{x.timestamp}</TableElement>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            <Widget
              src="roshaan.near/widget/Paginate-fork"
              props={{
                siblingCount: 1,
                totalCount: state.logsCount,
                pageSize: LIMIT,
                onPageChange: onLogsPageChange,
                currentPage: state.logsPage,
              }}
            />
          </div>
        ) : (
          <Subheading> No data to show... </Subheading>
        )}
      </CardBody>
      <CardFooter></CardFooter>
