    line-height: 1.43;
    font-size: 13px;
    min-width: 32px;

    &.dots:hover {
        background-color: transparent;
        cursor: default;
    }
    &:hover {
        background-color: rgba(0, 0, 0, 0.04);
        cursor: pointer;
    }

    &.selected {
