};

const fieldSize = "3rem";
const Legend = styled.div`
        min-width: ${fieldSize};
        min-height: ${fieldSize};
        max-width: ${fieldSize};
        max-height: ${fieldSize};
        font-size: 1.6rem;
        font-weight: 600;
        display: flex;
        justify-content: center;
        align-items: center;
`;
const renderBoard = (board) => {
  return board.reverse().flatMap((row, rowIndex) => {
    let res = row.split("").map((c, colIndex) => {
      const background = (rowIndex + colIndex) % 2 === 0 ? "#ddd" : "#555";
      const Field = styled.span`
        min-width: ${fieldSize};
        min-height: ${fieldSize};
        max-width: ${fieldSize};
        max-height: ${fieldSize};
        background: ${background};

        img {
            min-width: 100%;
            min-height: 100%;
            max-width: 100%;
            max-height: 100%;
        }
        `;
      return <Field>{renderPiece(c)}</Field>;
    });
    res.unshift(<Legend>{8 - rowIndex}</Legend>);
    const LineBreak = styled.div`
        flex: 1 0 100%;
    `;
    res.push(<LineBreak />);
    if (rowIndex === board.length - 1) {
      res = res.concat([
        <Legend></Legend>,
        <Legend>A</Legend>,
        <Legend>B</Legend>,
        <Legend>C</Legend>,
        <Legend>D</Legend>,
        <Legend>E</Legend>,
        <Legend>F</Legend>,
        <Legend>G</Legend>,
        <Legend>H</Legend>,
      ]);
    }
    return res;
  });
};

