  Near.view(nearDevGovBadgesContractId, "get_badge", {
    badge_id: badgeId,
  }).badge_metadata;

if (!badgeMetadata) {
  return <>Loading...</>;
}

return (
  <>
    <div className="bg-white shadow rounded overflow-hidden">
      <div className="px-4 pt-0 pb-5 bg-dark position-relative">
        <div
