const labelsToIdSet = (labels) => {
  const ids = labels.map(
    (label) =>
      Near.view(nearDevGovGigsContractAccountId, "get_posts_by_label", {
        label,
      }) ?? []
  );
  const idsFlat = ids.flat(1);
  return new Set(idsFlat);
};

