  if (requiredLabels.length > 0) {
    return {
      label: cl,
      posts: allIds.filter(
        (i) => requiredPostsSet.has(i) && !excludedPostsSet.has(i)
      ),
    };
  } else {
    // No extra filtering is required.
    return { label: cl, posts: allIds };
  }
});

