const slugify = (text) => {
  return text
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "")
    .toLowerCase()
    .trim()
    .replace(/\s+/g, "-")
    .replace(/[^\w-]+/g, "")
    .replace(/--+/g, "-");
};

const submitForm = () => {
  Near.call(
    nearDevGovBadgesContractId,
    "mint_badge",
    {
      badge_id: state.badgeId,
      badge_metadata: state.badgeMetadata,
    },
    30000000000000,
    1
  );
};

